21.合并两个有序链表 func mergeTwoLists(list1 *ListNode, list2 *ListNode) *ListNode { // 思路 1 递归解法 mergeTwoLists(list1.Next, list2) or mergeTwoLists(list1, list2.Next) // 思路 2 遍历list1 list2: 初始化dummy节点， p p1 p2分别指向三个链表 dummy := &ListNode{} p, p1, p2 := dummy, list1, list2 for p1 != nil && p2 != nil { if p1.Val < p2.Val { p.Next = p1 p1 = p1.Next }else { p.Next = p2 p2 = p2.Next } p = p.Next } if p1 != nil { p.Next = p1 } if p2 != nil { p.Next = p2 } return dummy.Next
} 23.合并K个升序链表 func mergeKLists(lists []*ListNode) *ListNode { // 思路 二分递归合并 转换成 mergeTwoLists if len(lists) <=0 { return nil } if len(lists) == 1 { return lists[0] } mid := len(lists)/2 left := mergeKLists(lists[:mid]) right := mergeKLists(lists[mid:]) return mergeTwoLists(left, right)
} func mergeTwoLists(list1, list2 *ListNode) *ListNode { if list1 == nil { return list2 }else if list2 == nil { return list1 }else if list1.Val < list2.Val { list1.Next = mergeTwoLists(list1.Next, list2) return list1 }else { list2.Next = mergeTwoLists(list1, list2.Next) return list2 }
} 86.分隔链表 func partition(head *ListNode, x int) *ListNode { if head == nil { return nil } // 思路 合并两个链表的逆操作, // 定义两个链表 small large；之后将large 接与small之后 dummy1, dummy2 := &ListNode{}, &ListNode{} small, large :=dummy1, dummy2 for head != nil { if head.Val < x { small.Next = head small = small.Next }else { large.Next = head large = large.Next } head = head.Next } // 防止有环 leetcode 校验结果时有环会oom large.Next = nil small.Next = dummy2.Next return dummy1.Next
}

