876.链表的中间结点 func middleNode(head *ListNode) *ListNode { // 快慢指针 快指针两倍速 slow, fast := head, head for fast!=nil && fast.Next != nil { slow = slow.Next fast = fast.Next.Next } return slow
} 19.删除链表的倒数第 N 个结点 func removeNthFromEnd(head *ListNode, n int) *ListNode { if head == nil { return nil } // 快慢指针 // 注意点 头结点也可能被删 故初始化dummy dummy := &ListNode{} dummy.Next = head slow, fast := dummy, dummy // fast 先走n+1步 保证删除的时候是删除slow.Next for i:=0; i<=n && fast != nil; i++ { fast = fast.Next } for fast != nil { slow = slow.Next fast = fast.Next } // delete slow.Next slow.Next = slow.Next.Next return dummy.Next
} 61.旋转链表 func rotateRight(head *ListNode, k int) *ListNode { n := 0 p := head for p!=nil { p = p.Next n++ } if n == 0 || k%n == 0{ return head } p = head for i:=0; i<(n-1-k%n); i++ { p = p.Next } newHead := p.Next p.Next = nil p = newHead for p.Next != nil { p = p.Next } p.Next = head return newHead
} 83.删除排序链表中的重复元素 82.删除排序链表中的重复元素II func deleteDuplicates(head *ListNode) *ListNode { if head == nil { return nil } // 思路1 hash+两次遍历 // 思路2 指针 cur.Next == cur.Next.Next delete cur.Next = cur.Next.Next dummy := &ListNode{} dummy.Next = head cur := dummy for cur.Next != nil && cur.Next.Next !=nil { if cur.Next.Val == cur.Next.Next.Val { reaptVal := cur.Next.Val // delete reapt for cur.Next != nil && cur.Next.Val == reaptVal { cur.Next = cur.Next.Next } }else { cur = cur.Next } } return dummy.Next
