func dpF(nums) bool { // 定义 dp[i][j], 表示针对 nums[:i] 容量为 j, 能否恰好装满 // 则 对于当前的 dp[i][j] 取决于当前数字 nums[i] 是否装入 // 当前不装入，则从 nums[:i-1] 去选择： dp[i][j] = dp[i-1][j] // 当前装入，则 nums[:i-1] 可装入的容量变为 j- nums[i]： dp[i][j] = dp[i-1][j-nums[i]] // base case: dp[:][0] = true dp[0][1:] = false dp := make([][]bool, len(nums)+1) for i := 0; i <= len(nums); i++ { if dp[i] == nil { dp[i] = make([]bool, target+1) } for j := 0; j <= target; j++ { if j == 0 { dp[i][j] = true continue } if i == 0 { dp[i][j] = false continue } dp[i][j] = dp[i-1][j] if j-nums[i-1] >= 0 { dp[i][j] = dp[i][j] || dp[i-1][j-nums[i-1]] } } } return dp[len(nums)][target]
