} func dfs(grid [][]byte, i, j int) { // 到达边界 if i<0 || i>=len(grid) || j>=len(grid[i]) || j<0 { return } // 已经是水 if grid[i][j] == '0' { return } // 淹没 grid[i][j] = '0' // 淹没相邻陆地 dfs(grid, i+1, j) dfs(grid, i-1, j) dfs(grid, i, j+1) dfs(grid, i, j-1)
} 1905.统计子岛屿 func countSubIslands(grid1 [][]int, grid2 [][]int) int { // 思路 将grid2中的岛屿不存在grid1中的淹掉， 然后统计grid2岛屿的数量 for i:=0; i<len(grid2); i++ { for j:=0; j<len(grid2[i]); j++ { if grid2[i][j] == 1 && grid1[i][j] == 0 { // 淹没当前岛屿 dfs(grid2, i, j) } } } res := 0 for i:=0; i<len(grid2); i++ { for j:=0; j<len(grid2[i]); j++ { if grid2[i][j] == 1 { res++ dfs(grid2, i, j) } } } return res
} 1254.统计封闭岛屿的数目 func closedIsland(grid [][]int) int { // 思路 把靠边的岛屿先淹没， 然后统计岛屿数量 m := len(grid) n := len(grid[0]) for i:=0; i<n; i++ { dfs(grid, 0, i) dfs(grid, m-1, i) } for i:=0; i<m; i++ { dfs(grid, i, 0) dfs(grid, i, n-1) } res := 0 for i:=0; i<m; i++ { for j:=0; j<n; j++ { if grid[i][j] == 0 { res++ dfs(grid, i, j) } } } return res
} 1020.飞地的数量 func numEnclaves(grid [][]int) int { // 思路 对四周边界的岛屿进行淹没， 然后统计1的数量 m := len(grid) n := len(grid[0]) for i:=0; i<n; i++ { dfs(grid, 0, i) dfs(grid, m-1, i) } for i:=0; i<m; i++ { dfs(grid, i, 0) dfs(grid, i, n-1) } res := 0 for i:=0; i<m; i++ { for j:=0; j<n; j++ { if grid[i][j] == 1 { res++ } } } return res
} 695.岛屿的最大面积 func maxAreaOfIsland(grid [][]int) int { m := len(grid) n := len(grid[0]) var dfs func(grid [][]int, i, j int) int dfs = func(grid [][]int, i, j int) int { if i < 0 || j < 0 || i >= m || j >= n || grid[i][j] == 0 { return 0 } grid[i][j] = 0 area := 1 area += dfs(grid, i+1, j) area += dfs(grid, i-1, j) area += dfs(grid, i, j-1) area += dfs(grid, i, j+1) return area } // 统计 maxArea := 0 for i := 0; i < m; i++ { for j := 0; j < n; j++ { if grid[i][j] == 1 { area := dfs(grid, i, j) if area > maxArea { maxArea = area } } } } return maxArea
} 小结 岛屿问题基本思想：if grid[i][j]=='1' res++ 然后用dfs算法对相邻陆地进行淹没 left：dfs(grid, i, j-1) right: dfs(grid, i, j+1) top: dfs(grid, i-1, j) bootn: dfs(grid, i+1, j) 子岛屿问题先将grid2中的岛屿不存在Grid1中的进行淹没，然后统计岛屿数量 飞地问题/封闭岛屿问题， 先将四周边界的岛屿进行淹没，然后按题目需求进行求解

