func minPathSum(grid [][]int) int { // dp[i][j] 表示从(0, 0)出发到达(i, j)时的最小路径和 // 状态转移: (i, j) 的位置只能由(i-1, j) (i, j-1)而来， 问题转换为分别到达(i-1, j) (i, j-1)的最小路径和, 注意i, j遇到边界的处理 // dp[i][j] = min(dp[i-1][j], dp[i][j-1]) + grid[i][j] // base case: dp[0][0] = grid[0][0] // return dp[m-1][n-1] // dp[i][j] 表示从 (i, j) 出发， 到达最右下角时的最小路径和 // 要使 dp[i][j] 最小， 即 grid[i][j] + 下一步选取最小的路径即可, 下一步可选的路径是 dp[i+1][j] dp[i][j+1] // dp[i][j] = min(dp[i+1][j], dp[i][j+1]) + grid[i][j] 注意边界值 // base case dp[m-1][n-1] = grid[m-1][n-1] // return dp[0][0] m := len(grid) if m == 0 { return 0 } n := len(grid[0]) dp := make([][]int, m) for i:=m-1; i>=0; i-- { if dp[i] == nil { dp[i] = make([]int, n) } for j:=n-1; j>=0; j-- { if i == m-1 && j == n-1 { dp[i][j] = grid[i][j] }else if i == m-1 { dp[i][j] = dp[i][j+1] + grid[i][j] }else if j == n-1 { dp[i][j] = dp[i+1][j] + grid[i][j] }else { dp[i][j] = min(dp[i+1][j], dp[i][j+1]) + grid[i][j] } } } return dp[0][0]
} 120.三角形最小路径和 func minimumTotal(triangle [][]int) int { // 思路1 // dp[i][j] 表示从(i ,j)出发， 到达最底层时的最小路径和 // 状态转移：当前值+相邻下一层的最小值 // dp[i][j] = min(dp[i+1][j], dp[i+1][j+1]) + triangle[i][j] // base case: 最后一层dp[i][j] = triangle[i][j] // return dp[0][0] dp := make([][]int, len(triangle)) dp[len(triangle)-1] = triangle[len(triangle)-1] for i:=len(triangle)-2; i>=0; i-- { if dp[i] == nil { dp[i] = make([]int, len(triangle[i])) } for j:=0; j<len(dp[i]); j++ { if dp[i+1][j] < dp[i+1][j+1] { dp[i][j] = dp[i+1][j] + triangle[i][j] }else { dp[i][j] = dp[i+1][j+1] + triangle[i][j] } } } return dp[0][0] // 思路2 // dp[i][j] 表示从(0, 0)出发到达 (i, j)的最小路径和 // 状态转移 当前值+上一层相邻的最小路径和 // dp[i][j] = min(dp[i-1][j], dp[i-1][j-1]) + triangle[i][j] // base case dp[0][0] = triangle[0][0] // return min(dp[len(triangle)-1])
} 62.不同路径数 func uniquePaths(m int, n int) int { // dp[i][j] 表示从(0, 0)出发 到达(i, j)共有多少种路径 // 状态转移 (i, j)的位置只能由(i-1, j) (i, j-1)而来， 问题转换为分别到达(i-1, j) (i, j-1)的路径数量相加, 注意i, j遇到边界的处理 // dp[i][j] = dp[i-1][j]+dp[i][j-1] // base case dp[0][0] = 1 // return dp[m-1][n-1] dp := make([][]int, m) for i:=0; i<m; i++ { if dp[i] == nil { dp[i] = make([]int, n) } for j:=0; j<n; j++ { if i==0 && j==0 { dp[i][j] = 1 }else if i == 0 { dp[i][j] = dp[i][j-1] }else if j == 0 { dp[i][j] = dp[i-1][j] }else { dp[i][j] = dp[i-1][j] + dp[i][j-1] } } } return dp[m-1][n-1]
} 63不同路径数II func uniquePathsWithObstacles(obstacleGrid [][]int) int { // dp[i][j] 表示从(0, 0)出发 到达(i, j)共有多少种路径 // 状态转移 (i, j)的位置只能由(i-1, j) (i, j-1)而来， 问题转换为分别到达(i-1, j) (i, j-1)的路径数量相加, 当grid[i][j]==1 时， dp[i][j]=0; 注意i, j遇到边界的处理 // dp[i][j]= grid[i][j]==1? 0:dp[i-1][j]+dp[i][j-1] // base case dp[0][0] = 1 if grid[0][0] == 1 return 0 // return dp[m-1][n-1] } 小结 矩阵(三角形)类的动规问题通常需二维dp数组来存储状态。 dp[i][j]的定义基本为从起点出发到达(i, j)时的最大最小、总数之类的含义； 亦可定义为从(i, j)出发，到达终点时的所求值； 两种定义的差异体现在状态转移时 dp[i-1][j] dp[i][j-1]; dp[i+1][j] dp[i][j+1]

