70.爬楼梯 func climbStairs(n int) int { // dp[i] 表示到达第i阶有多少种方法 // 状态转移 dp[i] = dp[i-1] + dp[i-2] // base case 第0阶表示地面 dp[0] = 1 dp[1] = 1 // return dp[n]
} 55.跳跃游戏 func canJump(nums []int) bool { // 思路1 // dp[i] 表示从0出发 能否到达第i个下标 // 状态转移 dp[i] 依赖于所有 <i 的点当中是否有可以到达i的下标，即 dp[j] && nums[j]>=i-j // base case dp[0]=true // return dp[len(nums)-1] dp := make([]bool, len(nums)) for i:=0; i<len(nums); i++ { if i == 0 { dp[0] = true continue } for j:=0; j < i; j++ { if dp[j] && i-j <= nums[j] { dp[i] = true break } } } return dp[len(nums)-1] // 思路2 // dp[i] 表示从出发， 能否到达终点 // 状态转移 dp[i] 依赖于下标i所能到达的下一批点位中是否有能到达终点的下标， 即 for j<=nums[i]{ dp[i] = dp[i] || dp[i+j] } // base case dp[len(nums)-1] = true // return dp[0]
} 45.跳跃游戏II func jump(nums []int) int { // dp[i] 表示从nums[i] 到最后一个位置的最少次数 // 状态转移 dp[i] = min(dp[i+0], dp[i+1], ..., dp[i+nums[i]]) + 1 // base case dp[n-1] = 0 // dp[i] 表示到达 i 的最小跳跃次数 // dp[i] = min(dp[:i-1] && nums[j] >= i-j) + 1 // base case dp[0] = 0 dp := make([]int, len(nums)) for i:=0; i<len(nums); i++ { if i == 0 { dp[0] = 0 continue } dp[i] = math.MaxInt32 for j:=0; j<i; j++ { if nums[j] >= i-j && dp[i] > dp[j] { dp[i] = dp[j] + 1 } } } return dp[len(nums)-1]
} 小结 跳跃爬楼梯问题通常用一维dp表来存储状态。 定义基本为：从初始点到达当前点 或 从当前点到达终点 状态推导思路：从之前几个状态进行推导 mix max

