53.最大子数组和 func maxSubArray(nums []int) int { // dp[i] 表示以nums[i]为结尾的连续子数组，最大数组和 // 状态转移 如果dp[i-1]>0 dp[i]=dp[i-1]+nums[i] 否则 dp[i] = nums[i] // base case dp[0]=nums[0] // return max(dp[:]) dp := make([]int, len(nums)) dp[0] = nums[0] max := dp[0] for i:=1; i<len(nums); i++ { dp[i] = nums[i] if dp[i-1] > 0 { dp[i] = dp[i-1] + nums[i] } if dp[i]>max { max = dp[i] } } return max
} 300.最长递增子序列 func lengthOfLIS(nums []int) int { // dp[i] 表示以 nums[i] 为递增子序列的结尾，其所有子序列中的最大长度 // 状态转移 遍历nums[:i] if nums[j]<nums[i] dp[i] = max(dp[i], dp[j]+1) // base case dp[0] = 1 // return max(dp[:]) dp := make([]int, len(nums)) res := 0 for i:=0; i<len(nums); i++ { if i == 0 { dp[i] = 1 res = dp[i] continue } dp[i] = 1 for j:=0; j<i; j++ { if nums[i] > nums[j] && dp[i] < dp[j] +1 { dp[i] = 1 + dp[j] } } if dp[i] > res { res = dp[i] } } return res
} 131.分割回文串 回溯算法 func partition(s string) [][]string { var backtrack func(s string, pos int, track []string, res *[][]string) backtrack = func(s string, pos int, track []string, res *[][]string) { if pos == len(s) { ans := make([]string, len(track)) copy(ans, track) *res = append(*res, ans) return } for i:=pos; i<len(s); i++ { // 对于每一个 s[i] 都有两种选择：分割 or 不分割 if !isPalindrome(s[pos:i+1]) { continue } track = append(track, s[pos:i+1]) backtrack(s, i+1, track, res) track = track[:len(track)-1] } } track := make([]string, 0) res := make([][]string, 0) backtrack(s, 0, track, &res) return res
} func isPalindrome(s string) bool { if len(s) <= 1 { return true } i, j:= 0, len(s)-1 for i<j { if s[i] != s[j] { return false } i++ j-- } return true
} 132.分割回文串II 求最小分割次数 func minCut(s string) int { // dp[i] 表示将 s[:i] 分割成都是回文子串的最小分割次数 0<= i <= len(s) // 则 dp[i] = min(dp[j] && s[j:i] isPalindrome) + 1 // base case dp[0] = 0 dp[1] = 0 dp[i] = i-1 // return dp[len(s)] isPalindrome := func(s string) bool { for i,j:=0, len(s)-1; i<j; i, j= i+1, j-1{ if s[i] != s[j] { return false } } return true } huiwen := make([][]bool, len(s)+1) for i:=0; i<len(s); i++ { if huiwen[i] == nil { huiwen[i] = make([]bool, len(s)+1) } for j:=i; j<=len(s); j++ { huiwen[i][j] = isPalindrome(s[i:j]) } } dp := make([]int, len(s)+1) for i:=0; i<=len(s); i++ { if i <= 1 || huiwen[0][i] { dp[i] = 0 continue } dp[i] = i - 1 for j:=0; j<i; j++ { if huiwen[j][i] && dp[j] + 1 < dp[i] { dp[i] = dp[j] + 1 } } } return dp[len(s)]
} 32.最长有效括号 // dp[i] 表示以 s[i] 为结尾的最长有效括号长度 // 则 // 1. 若 s[i] == '(' 以 s[i] 为结尾的最长有效括号为必定为 0 ： dp[i] = 0 // 2. 若 s[i] == ')' 需根据 s[i-1] 做状态转移 // 2.1 若 s[i-1] == '(' 则 s[i-1] s[i] 已能组成有效括号 dp[i] = dp[i-2] + 2 // 2.2 若 s[i-1] == ')' 则 需判断 dp[i-1] // 2.2.1 若 dp[i-1] == 0 则 dp[i] =0 // 2.2.2 若 dp[i-1] > 0 && i - dp[i-1]>0 && s[i - dp[i-1] -1 ] == '(' 则 dp[i] = dp[i-1] + 2 + dp[i- dp[i-1] -2] // (subs) ( (sub_s) ) // base case: dp[0] = 0 // return max(dp[:]) dp := make([]int, len(s)) res := 0 for i :=0; i<len(s); i++ { if i == 0 { dp[i] = 0 continue } if i == 1 { if s[i] == ')' && s[i-1] == '(' { dp[i] = 2 res = dp[i] }else { dp[i] = 0 } continue } if s[i] == ')' && s[i-1] == '(' { dp[i] = dp[i-2] + 2 }else if s[i] == ')' && s[i-1] == ')' && dp[i-1] > 0 && i - dp[i-1] -1 >= 0 && s[i - dp[i-1] -1 ] == '(' { dp[i] = dp[i-1] + 2 if i-dp[i-1]-2>= 0 { dp[i] = dp[i] + dp[i- dp[i-1] -2] } } if dp[i] > res { res = dp[i] } } return res 139.单词拆分 func wordBreak(s string, wordDict []string) bool { // dp[i] 表示s[:i]能否由wordDict拼接出 // 状态转移 遍历s[:i] dp[i] = (s[:i] in wordDict) || (dp[j] && s[j:i] in wordDict) // base case dp[0] = true // return dp[len(s)] dict := make(map[string]bool) for _, word := range wordDict { dict[word] = true } dp := make([]bool, len(s)+1) for i:=0; i<=len(s); i++ { if i == 0 { dp[0] = true continue } for j:=0; j<i; j++ { if dp[j] && dict[s[j:i]] { dp[i] = true break } } } return dp[len(s)]
} 小结 动规子序列/子串问题通常用一维dp表来存储状态。问题7，最长回文子串不适于本分类，需要用二维数组来表示状态，此处仅做为记录。 定义基本为：一维数组dp[i], 表示将当前点作为结尾的结果，然后对s[i] 或者s[:i]做复合题意的判断

