1143.最长公共子序列 func longestCommonSubsequence(text1 string, text2 string) int { // dp[i][j] 表示text1[:i] text2[:j] 的最长公共子序列长度 // 状态转移 // text1[i] == text2[j] dp[i][j] = dp[i-1][j-1] + 1 // text1[i] != text2[j] dp[i][j] = max(dp[i-1][j], dp[i][j-1]) // base case dp[0][0] = 0 dp[i][0] = 0 dp[0][j] = 0 // return dp[len(text1)][len(text2)] dp := make([][]int, len(text1)+1) for i:=0; i<=len(text1); i++ { if dp[i] == nil { dp[i] = make([]int, len(text2)+1) } for j:=0; j<=len(text2); j++ { if i==0 || j == 0 { dp[i][j] = 0 continue } if text1[i-1] == text2[j-1] { dp[i][j] = dp[i-1][j-1] + 1 }else if dp[i-1][j] > dp[i][j-1] { dp[i][j] = dp[i-1][j] }else { dp[i][j] = dp[i][j-1] } } } return dp[len(text1)][len(text2)]
} 最长公共子串 func longestCommonSubstring(str1 string, str2 string) string { // dp[i][j] 表示以 str1[i] str2[j]结尾的最长公共子串 // 状态转移 // str1[i] == str2[j] dp[i][j] = dp[i-1][j-1] + str1[i:i+1] // str1[i] != str2[j] dp[i][j] = "" // base case dp[i][0] = "" dp[0][j] = "" dp[0][0] = str1[0]==str2[0]?str1[0:1]:"" // return max(dp[i][j])
} 72.编辑距离 func minDistance(word1 string, word2 string) int { // 思路1 // dp[i][j] 表示将 word1[:i] 变成 word2[:j] 所需的最少操作 // 状态转移 // 1. word1[i-1] == word2[j-1] 无需操作 dp[i][j] = dp[i-1][j-1] // 2. word1[i-1] != word2[j-1] 插入、删除、替换 取最小 dp[i][j] = min(dp[i][j-1], dp[i-1][j], dp[i-1][j-1]) + 1 // base case dp[0][0] = 0 dp[i][0] = i dp[0][j] = j // return dp[len(word1)][len(word2)] // 思路2 // max(len(str1), len(str2)) - LCS // LCS算法参考最长公共子序列 dp := make([][]int, len(str1)+1) for i:=0; i<=len(str1); i++ { //fmt.Println(i) if dp[i] == nil { dp[i] = make([]int, len(str2)+1) } for j:=0; j<=len(str2); j++ { if i== 0 { dp[i][j] = j continue }else if j == 0 { dp[i][j] = i continue } if str1[i-1] == str2[j-1] { dp[i][j] = dp[i-1][j-1] }else { dp[i][j] = min(dp[i][j-1], dp[i-1][j], dp[i-1][j-1]) + 1 } } } return dp[len(str1)][len(str2)]
} 10.正则表达式 // dp[i][j] 表示 s[:i] p[:j] 能否匹配 // 则存在如下状态转换 // p[j-1] == s[i-1] 匹配掉当前字符 dp[i][j] = dp[i-1][j-1] // p[j-1] == '.' 仅能匹配一个任意字符 dp[i][j] = dp[i-1][j-1] // p[j-1] == '*' 匹配0个p[j-2]字符 dp[i][j] = dp[i][j-2] || dp[i-1][j-2] || dp[i-1][j] // p[j-1] != '.' && p[j-1] != '*' dp[i][j] = false // base case dp[i][0] = i==0 dp[0][j] = j >= 2 && j%2 == 0 && p[j-1] == '*' false : dp[i][j-2] dp := make([][]bool, len(s)+1) for i:=0; i<=len(s); i++ { if dp[i] == nil { dp[i] == make([]bool, len(p)+1) } for j:=0; j<=len(p); j++ { if j == 0 { dp[i][j] = i==0 continue } if i == 0 { dp[i][j] = false if j >= 2 && j%2 == 0 && p[j-1] == '*' { dp[i][j] = dp[i][j-2] } continue } if p[j-1] == s[i-1] { dp[i][j] = dp[i-1][j-1] }else if p[j-1] == '.' { dp[i][j] = dp[i-1][j-1] }else if p[j-1] == '*' { dp[i][j] = dp[i][j-2] if p[j-2] == s[i-1] || p[j-2] == '.' { dp[i][j] = dp[i][j-2] || dp[i-1][j-2] || dp[i-1][j] } }else { dp[i][j] = false } } } return dp[len(s)][len(p)] 28.strstr / KMP算法 小结 两个序列的动规问题，需定义一个二维dp表， 所代表的意义一般是 str1[:i] str2[:j] 的最值 状态转移一般 dp[i][j] = min/max(dp[i-1][j], dp[i][j-1], dp[i-1][j-1]) 此类型的方程 需注意dp的长度通常会Len(str1)+1

